import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import React, { useState } from 'react';

import axios from 'axios';

function Auth() {
    
    const [usernameReg, setUsernameReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');

    const register = ()=>{
        axios.post('http://localhost:3001/register',{
        username: usernameReg,
        password: passwordReg,
        }).then((response)=> {console.log(response)});

        
    }


        return (
 
            <div className="Auth">
                   <div> <a  href='/regster'> <button onClick={register} className='btn btn-primary'> Register </button></a></div>
                <div className='contain'> 
              
                    <form className='form2'>
                        <h1>Registration</h1>
                        <label >username</label>
                        <input className="form-control" type="text" 
                         placeholder='enter your username' onChange={(e)=>{setUsernameReg(e.target.value)}}/>
                        <label >password</label>
                        <input className="form-control" type="text"  
                        placeholder='enter your password' onChange={(e)=>{setPasswordReg(e.target.value)}}/>
                       <div> <a  href='/regster'> <button onClick={register} className='btn btn-primary'> Register </button></a></div>
                    </form>
                    <form className='form1'>
                        <h1>Login</h1>
                   
                        <input className="form-control" type="text" 
                        placeholder='enter your username' onChange={(e)=>{setUsernameReg(e.target.value)}}/>
             
                        <input className="form-control" type="text" 
                        placeholder='enter your password' onChange={(e)=>{setPasswordReg(e.target.value)}}/>
                        <a href='/'> <button className='btn btn-primary' > Register </button></a>

                    </form>
                </div>
            </div>
    );

}
export default Auth;
