import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import React, { useState } from 'react';

import axios from 'axios';

function Auth() {
    
    const [usernameReg, setUsernameReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const [loginStatus, setLoginStatus] = useState('');

    const register = ()=>{
        axios.post('http://localhost:3001/register',{
        username: usernameReg,
        password: passwordReg,
        }).then((response)=> {console.log(response)});
    };

    
    const login = ()=>{
        axios.post('http://localhost:3001/login',{
        username: username,
        password: password,
        }).then((response)=> {
            if(response.data.message){
                setLoginStatus(response.data.message);
            }else{
                setLoginStatus(response.data[0].username);
                alert(loginStatus);
            }
        });

        
    }



        return (
 
            <div className="Auth">
                
                <div className='contain'> 
              
                    <div className='form2'>
                        <h1>Registration</h1>
                        <label >username</label>
                        <input className="form-control" type="text" 
                         placeholder='enter your username' onChange={(e)=>{setUsernameReg(e.target.value)}}/>
                        <label >password</label>
                        <input className="form-control" type="text"  
                        placeholder='enter your password' onChange={(e)=>{setPasswordReg(e.target.value)}}/>
                       <div> <a  href='/'> <button onClick={register} className='btn btn-primary'> Register </button></a></div>
                    </div>
                    <div className='form1'>
                        <h1>Login</h1>
                   
                        <input className="form-control" type="text" 
                        placeholder='enter your username' onChange={(e)=>{setUsername(e.target.value)}}/>
             
                        <input className="form-control" type="text" 
                        placeholder='enter your password' onChange={(e)=>{setPassword(e.target.value)}}/>
                        <a href='/'> <button className='btn btn-primary' onClick={login}> Register </button></a>

                    </div>
                </div>
                <div>{loginStatus}</div>
            </div>
    );

}                                                                    
export default Auth;
