import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import { useState } from 'react';

function App() {
  const [animeForChapter, setAnimeForChapter] = useState('');
  const [updateManga, setUpdateManga] = useState('');
  const [lastChapter, setLastChapter] = useState('');
  const titleURL = ' https://api.mangadex.org/manga?title=';
 const chapterURL =  'https://api.mangadex.org/manga/{id}/feed';


 const getChapter = () => {
   const theurl = ' https://api.mangadex.org/manga?title='+ animeForChapter;
  fetch(theurl)
  .then((response)=>response.json())
  .then((data)=>{
    if(data.data.length > 11)
 {   console.log(data)}
