          <input type="text" placeholder="name of anime"  className="form-control"/>
          <input type="text" placeholder="enter the chapter"  className="form-control"/>
        </div>
        <button className="history" className='btn btn-primary'>Search</button>
      </div>
    </div>
  );
}

export default App;
