import '../App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import React, { useState } from 'react';

function Auth() {
    
    const [usernameReg, setUsernameReg] = useState('');
    const [passwordReg, setPasswordReg] = useState('');


        return (
 
            <div className="Auth">
                
                <div className='contain'> 
              
                    <form className='form2'>
                        <h1>Registration</h1>
                        <label >username</label>
                        <input className="form-control" type="text" 
                         placeholder='enter your username' onChange={(e)=>{setUsernameReg(e.target.value)}}/>
                        <label >password</label>
                        <input className="form-control" type="text"  placeholder='enter your password'/>
                        <a  href='/'> <button className='btn btn-primary'> Register </button></a>
                    </form>
                    <form className='form1'>
                        <h1>Login</h1>
                   
                        <input className="form-control" type="text" placeholder='enter your username' />
             
                        <input className="form-control" type="text" placeholder='enter your password'/>
                        <a href='/'> <button className='btn btn-primary'> Register </button></a>

                    </form>
                </div>
            </div>
    );

}
export default Auth;
